import { useEffect, useState } from "react";

const FreeAssessment = () => {
  const [opacity, setOpacity] = useState(0.2);
  const [isFixed, setIsFixed] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const carousel = document.getElementById("carouselExampleFade");
      if (!carousel) return;

      const carouselPosition = carousel.getBoundingClientRect().top;
      const triggerPoint = window.innerHeight * 0.6;

      // Fade in effect near carousel
      if (carouselPosition < triggerPoint) {
        let newOpacity = Math.min(1, (triggerPoint - carouselPosition) / 200);
        setOpacity(newOpacity);
      } else {
        setOpacity(0.2);
      }

      // Fix it at the bottom when scrolled past carousel
      if (carouselPosition < 100) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div
      className={`freas ${isFixed ? "fixed" : ""}`}
      style={{ opacity, transition: "opacity 0.5s ease-in-out" }}
    >
      <div className="content">
        <div className="text-container">
          <h1>Free Assessment</h1>
          <p>
            We offer free career counseling and career planning advice to study abroad 
            and settle in leading countries like the USA, UK, Canada, Australia, and more.
          </p>
        </div>
        <button className="assessment-btn">Free Assessment</button>
      </div>
    </div>
  );
};

export default FreeAssessment;
