import React, { useState } from "react";
import "./study.css";
import { Link } from "react-router-dom";
import logo3 from "../images/l.png";
import glob from "../images/globes.png";


import mapboxgl from "mapbox-gl";

const makeCall = () => {
  window.location.href = "tel:+6281187094";
};

function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    message: "",
  });

  const [map, setMap] = useState(null);
  const [zoom, setZoom] = useState(12);

  // Mapbox Token
  mapboxgl.accessToken =
    "YOUR_MAPBOX_API_KEY"; // Replace with your Mapbox API key

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const mailtoLink = `mailto:your-email@example.com?subject=Free Assessment Request&body=
        Name: ${formData.name}%0D%0A
        Email: ${formData.email}%0D%0A
        Phone: ${formData.phone}%0D%0A
        Message: ${formData.message}`;
    window.location.href = mailtoLink;
  };

  // Initialize map when component loads
  React.useEffect(() => {
    const initMap = new mapboxgl.Map({
      container: "map", // Container ID
      style: "mapbox://styles/mapbox/streets-v11",
      center: [78.4483, 17.4375], // Initial location (Hyderabad)
      zoom: zoom,
    });

    // Add a marker at Hyderabad
    new mapboxgl.Marker()
      .setLngLat([78.4483, 17.4375])
      .setPopup(new mapboxgl.Popup().setHTML("<h4>Hyderabad</h4>"))
      .addTo(initMap);

    setMap(initMap);
  }, []);

  // Navigate to Ameerpet
  const navigateToAmeerpet = () => {
    map.flyTo({
      center: [78.4483, 17.4375],
      zoom: 15,
      essential: true,
    });
  };

  // Zoom In & Out Functions
  const handleZoomIn = () => {
    map.zoomIn();
  };

  const handleZoomOut = () => {
    map.zoomOut();
  };

  return (
    <div className="usmin">
      {/* Navbar Section */}
      <div className="logsec">
        <img
          style={{
            height: "60px",
            width: "200px",
            marginTop: "-10px",
            marginLeft: "70px",
          }}
          src={logo3}
          alt="lg"
        />
        <img
          style={{
            height: "100px",
            width: "330px",
            marginLeft: "300px",
            marginTop: "-10px",
          }}
          className="imgt"
          src={glob}
          alt="glb"
        />
        <button
          onClick={makeCall}
          style={{ marginLeft: "460px", borderRadius: "20%" }}
        >
          Contact us
        </button>
      </div>

      <header className="header">
        <nav className="custom-navbar">
          <ul className="nav-links">
            <li>
              <Link to="/" className="home-button">
                Home
              </Link>
            </li>
            <li>
              <Link to="/about">About Us</Link>
            </li>
            <li>
              <Link to="/studyloans">Study Loans</Link>
            </li>
            <li>
              <Link to="/englishtests">English Tests</Link>
            </li>
            <li>
              <Link to="/contact">Contact Us</Link>
            </li>
          </ul>
        </nav>
      </header>

      <div className="contact-container">
        <div className="form-section">
          <h2>Leave a Message Here</h2>
          <p>We will connect you to VisaPress Consultant ASAP.</p>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Name *</label>
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Mobile No *</label>
              <input
                type="text"
                name="mobile"
                placeholder="Mobile No"
                value={formData.mobile}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Email Address *</label>
              <input
                type="email"
                name="email"
                placeholder="Enter Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Subject</label>
              <input
                type="text"
                name="subject"
                placeholder="Subject"
                value={formData.subject}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Message</label>
              <textarea
                name="message"
                placeholder="Message"
                value={formData.message}
                onChange={handleChange}
              />
            </div>
            <button type="submit" className="submit-btn">
              SUBMIT
            </button>
          </form>
        </div>

        <div className="contact-info">
          <h3>Quick Contact</h3>
          <p>
            We are pleased to speak with you to discuss your qualifications and
            options under the various immigration programs and answer any
            questions or concerns you may have.
          </p>
          <h4>Phone</h4>
          <p>Phone: 8978779676</p>
          <p>Land Line: 040 45174985</p>

          <h4>Inquire with us</h4>
          <p>info@unimartoverseas.com</p>

          <h4>Send your feedback</h4>
          <p>support@unimartoverseas.com</p>

          <h4>Head Office</h4>
          <p>Ameerpet.</p>
        </div>
      </div>

      {/* Map Section */}
      <div className="map-container">
        <div
          className="map-button"
          onClick={navigateToAmeerpet}
          title="Go to Ameerpet"
        >
          📍 Go to Ameerpet
        </div>
        <div id="map" className="map"></div>
        <div className="zoom-controls">
          <button onClick={handleZoomIn}>➕</button>
          <button onClick={handleZoomOut}>➖</button>
        </div>
      </div>
    </div>
  );
}

export default Contact;
