import React from 'react';
import logo from '../images/logo.png';
import banner from '../images/backimg.png';
import '../../App.css';

const makeCall = () => {
  window.location.href = "tel:+6281187094";
};

function Home() {
  return (
    <div className="hmain">
      {/* Marquee Section */}
      <div className="marquee-container">
        <div className="marquee-content">
          Welcome To <span>UNIMART OVERSEAS</span> - A Leading Immigration Company üåç‚úàÔ∏è
        </div>
      </div>

      {/* Banner Section */}
      <div className="banner-container">
        <img className="banner-img" src={banner} alt="BannerImage" />
      </div>

      {/* Logo and Text Section */}
      <div className="hmsec">
        <img className="logo-img" src={logo} alt="Homelogo" />
        <h1 className="hf1">Study in Best Universities Across the World</h1>
        <p className="hf2">Take the First Step to STUDY ABROAD</p>
      </div>

      {/* Call to Action Button */}
      <button onClick={makeCall} className="bk">
        Book your free consultation
      </button>

     

      {/* Second Page Content */}
      <div className='pg2'>
        <h2 className='hd2'>
          Start Your Higher Education Journey with AbroadOptions Overseas Consultants and open yourself
          to a whole new world full of opportunities.
        </h2>
         {/* Navbar with Dropdown */}
      <header className="header">
        <nav className="navbar">
          <ul className="nav-links">
            <li className="dropdown">
              <a href="#">Study in Abroad</a>
              <ul className="dropdown-menu">
                <li><a href="#">Study in USA</a></li>
                <li><a href="#">Study in UK</a></li>
                <li><a href="#">Study in Canada</a></li>
                <li><a href="#">Study in Australia</a></li>
              </ul>
            </li>
            <li><a href="#">About Us</a></li>
            <li><a href="#">Study Loans</a></li>
            <li><a href="#">English Tests</a></li>
            <li><a href="#">Blog</a></li>
            <li><a href="#">Contact Us</a></li>
          </ul>
        </nav>
      </header>
      <h1></h1>
      </div>
    </div>
  );
}

export default Home;
