import React,{use} from 'react';
import logo from '../images/logo.png';
import banner from '../images/backimg.png';
import '../../App.css';
// import "bootstrap/dist/css/bootstrap.min.css";  
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const makeCall = () => {
    window.location.href = "tel:+6281187094";
  };
  
  function Home() {
    useEffect(() => {
      const carousel = document.querySelector("#carouselExampleFade");
  
      if (carousel) {
        const bsCarousel = new window.bootstrap.Carousel(carousel, {
          interval: 3000, // Change slide every 3 seconds
          ride: "carousel", // Auto slide
        });
  
        carousel.addEventListener("mouseenter", () => {
          bsCarousel.pause(); // Pause on hover
        });
  
        carousel.addEventListener("mouseleave", () => {
          bsCarousel.cycle(); // Resume auto slide on mouse leave
        });
      }
    }, []);
  
    return (
      <div className="hmain">
        {/* Marquee Section */}
        <div className="marquee-container">
          <div className="marquee-content">
            Welcome To <span>UNIMART OVERSEAS</span> - A Leading Immigration Company üåç‚úàÔ∏è
          </div>
        </div>
  
        {/* Banner Section */}
        <div className="banner-container">
          <img className="banner-img" src={banner} alt="BannerImage" />
        </div>
  
        {/* Logo and Text Section */}
        <div className="hmsec">
          <img className="logo-img" src={logo} alt="Homelogo" />
          <h1 className="hf1">Study in Best Universities Across the World</h1>
          <p className="hf2">Take the First Step to STUDY ABROAD</p>
        </div>
  
        {/* Call to Action Button */}
        <button onClick={makeCall} className="bk">
          Book your free consultation
        </button>
  
        {/* Second Page Content */}
        <div className='pg2'>
          <h2 className="hd2">
            Start Your Higher Education Journey with 
            <span className="highlight"> AbroadOptions Overseas Consultants</span> 
            and open yourself to a whole new world full of opportunities.
          </h2>
  
          {/* Bootstrap Carousel */}
          <div id="carouselExampleFade" className="carousel slide carousel-fade">
            <div className="carousel-inner">
              <div className="carousel-item active">
                <img src="https://via.placeholder.com/800x400/555/fff?text=First+Slide" className="d-block w-100" alt="First slide" />
              </div>
              <div className="carousel-item">
                <img src="https://via.placeholder.com/800x400/666/fff?text=Second+Slide" className="d-block w-100" alt="Second slide" />
              </div>
              <div className="carousel-item">
                <img src="https://via.placeholder.com/800x400/777/fff?text=Third+Slide" className="d-block w-100" alt="Third slide" />
              </div>
            </div>
            <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="prev">
              <span className="carousel-control-prev-icon" aria-hidden="true"></span>
              <span className="visually-hidden">Previous</span>
            </button>
            <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="next">
              <span className="carousel-control-next-icon" aria-hidden="true"></span>
              <span className="visually-hidden">Next</span>
            </button>
          </div>
        </div>
      </div>
    );
  }
  
  export default Home;
